bool utility_isPlaying;
int utility_buildIndex;
int utility_buildLength;
uint8_t * utility_buildFile;

bool utilityIsPlaying(void)/*{{{*/
{
	return utility_isPlaying;
}/*}}}*/

bool utilityPlaybackHasNext()/*{{{*/
{
	return ( utility_buildIndex < utility_buildLength ); 
}/*}}}*/

uint8_t utilityPlaybackNext()/*{{{*/
{
	uint8_t byte;
	if(utility_buildIndex < utility_buildLength)
	{
		byte=utility_buildFile[utility_buildIndex++];
		return byte;
	}
	else
		return 0;
}/*}}}*/
bool utilityStartPlayback(uint8_t build)/*{{{*/
{
	utility_isPlaying = true;
	utility_buildIndex = 0;
	switch(build)
	{
		case US_HOME_AXES:
			utility_buildFile = us_HomeAxes;
			break;
		case US_LEVEL_PLATE_SECOND:
			utility_buildFile= us_LevelPlate;
			build = US_LEVEL_PLATE_STARTUP;
			utilityGetSecondLevelOffset();
			break;
		case US_LEVEL_PLATE_STARTUP:
			utility_buildFile=us_LevelPlate;
			break;
		case US_TOOLHEAD_CALIBRATE:
			utility_buildFile=us_NozzleCalibrate;
			break;
		default:
			return false;
	}
	utility_buildLength=us_Lengths[build];
	cmdPaused=false;
	return utility_isPlaying;
}/*}}}*/
void utilityFinishPlayback()/*{{{*/
{
	utility_isPlaying=false;
}/*}}}*/
void utiltiyReset()/*{{{*/
{
	utility_buildIndex=0;
	utility_buildLength=0;
	utility_isPlaying=false;
}/*}}}*/
void utilityGetSecondLevelOffset()/*{{{*/
{
	uint8_t msgCnt=0;
	while(msgCnt<5)
	{
		while(utility_buildFile[utility_buildIndex] != 149)
			utility_buildIndex++;
		utility_buildIndex++;
		msgCnt++;
	}
	utility_buildIndex--;
}/*}}}*/

